import React, { useEffect, useState } from 'react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                setLoading(true);
                setError(null);

                const response = await fetch('http://localhost:5000/api/messages');

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const result = await response.json();

                // Handle the new API response format
                if (result.success && result.data) {
                    setMessages(result.data);
                } else if (Array.isArray(result)) {
                    // Fallback for old format
                    setMessages(result);
                } else {
                    throw new Error('Invalid response format');
                }
            } catch (err) {
                console.error('Error fetching messages:', err);
                setError(`Failed to load messages: ${err.message}`);
            } finally {
                setLoading(false);
            }
        };

        fetchMessages();
    }, []);

    const addMessage = async (message) => {
        try {
            setError(null);

            console.log('Sending message:', message);

            // Check if message object exists
            if (!message || typeof message !== 'object') {
                throw new Error('Invalid message data: message object is required');
            }

            // Fix: Use actual message data with proper null checks
            const username = message.username || message.user || 'Anonymous';
            const content = message.content || message.message || message.text || '';

            const messageData = {
                username: username,
                content: content
            };

            console.log('Mapped message data:', messageData);

            // Validate client-side before sending with proper null checks
            if (!messageData.username || !messageData.username.trim()) {
                throw new Error('Username is required');
            }

            if (!messageData.content || !messageData.content.trim()) {
                throw new Error('Message content is required');
            }

            const response = await fetch('http://localhost:5000/api/messages', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(messageData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            console.log('Server response:', result);

            // Handle the server response format
            if (result.success && result.data) {
                setMessages((prevMessages) => [result.data, ...prevMessages]);
            } else if (result.data) {
                // Handle case where success field might be missing
                setMessages((prevMessages) => [result.data, ...prevMessages]);
            } else if (result._id) {
                // Handle direct message object response
                setMessages((prevMessages) => [result, ...prevMessages]);
            } else {
                console.error('Unexpected response format:', result);
                throw new Error('Invalid response format from server');
            }
        } catch (err) {
            console.error('Error sending message:', err);
            setError(`Failed to send message: ${err.message}`);
        }
    };

    if (loading) {
        return <div>Loading messages...</div>;
    }

    return (
        <div>
            {error && (
                <div style={{ color: 'red', marginBottom: '10px', padding: '10px', border: '1px solid red' }}>
                    {error}
                    <button onClick={() => setError(null)} style={{ marginLeft: '10px' }}>
                        Dismiss
                    </button>
                </div>
            )}
            <MessageList messages={messages} />
            <MessageInput onSend={addMessage} />
        </div>
    );
};

export default Chat;
